mapper.select:
    SELECT 
    FROM t_guard_area tga
	WHERE tga.nation_code=#{nationCode,jdbcType=VARCHAR}
	AND tga.province_code=#{provinceCode,jdbcType=VARCHAR}
	AND tga.city_code=#{cityCode,jdbcType=VARCHAR}
	AND tga.area_code=#{areaCode,jdbcType=VARCHAR}
	AND tga.is_del=0


select 

from t_guard_area
where 1=1 

 AND nation_code = #{nationCode,jdbcType=VARCHAR}


AND  province_code = #{provinceCode,jdbcType=VARCHAR}


AND  city_code = #{cityCode,jdbcType=VARCHAR}


AND  area_code = #{areaCode,jdbcType=VARCHAR}


 AND emp_code = #{empCode,jdbcType=VARCHAR}


AND  emp_phone = #{empPhone,jdbcType=VARCHAR}


AND  emp_name = #{empName,jdbcType=VARCHAR}


AND   create_fid = #{createFid,jdbcType=VARCHAR}


AND  is_del = #{isDel,jdbcType=INTEGER}

ORDER BY create_date DESC


select 

from t_guard_area
where 1=1 

 AND nation_code = #{nationCode,jdbcType=VARCHAR}


AND  province_code = #{provinceCode,jdbcType=VARCHAR}


AND  city_code = #{cityCode,jdbcType=VARCHAR}


AND  area_code = #{areaCode,jdbcType=VARCHAR}


 AND emp_code = #{empCode,jdbcType=VARCHAR}


AND  emp_phone = #{empPhone,jdbcType=VARCHAR}


AND  emp_name = #{empName,jdbcType=VARCHAR}


AND   create_fid = #{createFid,jdbcType=VARCHAR}


AND  is_del = #{isDel,jdbcType=INTEGER}

ORDER BY last_modify_date
