mapper.select:

 select 

 from t_guard_area_log
 where 1=1

 AND  gurad_area_fid = #{guradAreaFid,jdbcType=VARCHAR}


  AND old_nation_code = #{oldNationCode,jdbcType=VARCHAR}


 AND  old_province_code = #{oldProvinceCode,jdbcType=VARCHAR}


  AND old_city_code = #{oldCityCode,jdbcType=VARCHAR}


 AND  old_area_code = #{oldAreaCode,jdbcType=VARCHAR}


 AND  guard_code = #{guardCode,jdbcType=VARCHAR}


 AND  guard_name = #{guardName,jdbcType=VARCHAR}


 AND  create_fid = #{createFid,jdbcType=VARCHAR}

ORDER BY create_date DESC
