mapper.select:
  	SELECT IF(MAX(bed_sn) IS NULL,99,MAX(bed_sn))  FROM t_house_bed_msg 
  	WHERE room_fid=#{roomFid,jdbcType=VARCHAR}
	AND is_del=0

  	SELECT bed_type,bed_size,COUNT(id) bedNum FROM t_house_bed_msg
	WHERE is_del=0

		AND house_base_fid=#{houseBaseFid,jdbcType=VARCHAR}


		AND room_fid=#{roomFid,jdbcType=VARCHAR}

	GROUP BY bed_type,bed_size

	SELECT COUNT(bed.`id`) FROM `t_house_room_msg` ro 
		     INNER JOIN `t_house_bed_msg` bed ON ro.`fid` = bed.`room_fid`
     WHERE  bed.is_del = 0
        AND ro.is_del = 0

		AND bed.`house_base_fid` =#{houseBaseFid,jdbcType=VARCHAR}


		AND bed.room_fid=#{roomFid,jdbcType=VARCHAR}

  	SELECT   FROM t_house_bed_msg hbm
	WHERE hbm.is_del=0
	AND hbm.house_base_fid=#{houseBaseFid,jdbcType=VARCHAR}
	AND hbm.room_fid=#{roomFid,jdbcType=VARCHAR}
	AND hbm.bed_type=#{bedType,jdbcType=INTEGER}
