mapper.delete:
update cf_zm_config
set  is_del = 1 
where system_id = #{systemId}  and cf_key = #{cfKey}

mapper.insert:
   update cf_zm_config 
	set  
       cf_value = #{cfValue,jdbcType=VARCHAR}, 
       cf_desc =  #{cfDesc,jdbcType=VARCHAR}, 
       last_modifytime =#{lastModifytime,jdbcType=TIMESTAMP}
   where 
   	system_id =  #{systemId,jdbcType=VARCHAR} 
		and cf_key = #{cfKey,jdbcType=VARCHAR}
		and is_del=0

mapper.select:
  select 

  from cf_zm_config 
  where is_del=0

			and system_id = #{systemId} 


			and cf_key = #{cfKey}
